mapper.select:
SELECT COUNT(*)
FROM member m
LEFT OUTER JOIN shop s
	ON m.member_id = s.member_id
WHERE m.member_status = 'Y'

		
		AND m.member_id LIKE #{search}
	
		
		AND  m.member_nick LIKE #{search}
	
		
		AND  s.shop_idx LIKE #{search}

SELECT t.member_id, t.member_nick, t.shop_name, t.coin_total, t.member_email, t.member_phone, t.member_birth, t.member_date, t.member_status FROM (SELECT m.member_id, m.member_nick, s.shop_name, c.coin_total, m.member_email, m.member_phone, m.member_birth, m.member_date, m.member_status FROM member m LEFT OUTER JOIN shop s ON m.member_id = s.member_id LEFT OUTER JOIN (SELECT member_id, coin_total FROM coin WHERE (member_id, coin_date) IN ( SELECT member_id, MAX(coin_date) AS coin_date FROM coin GROUP BY member_id)) c ON c.member_id = m.member_id) t WHERE t.member_status = 'Y'
AND t.member_id LIKE #{search}
AND t.member_nick LIKE #{search}
AND t.shop_name LIKE #{search}
GROUP BY t.member_id ORDER BY t.member_date DESC LIMIT #{pageInfo.startRow}, #{pageInfo.listLimit}

mapper.update:
UPDATE member
   SET member_status = 'N'
 WHERE member_id = #{member_id}

UPDATE member
   SET member_status = 'Y'
 WHERE member_id = #{member_id}

UPDATE ord
SET ord_status = 'C'
WHERE item_idx = #{item_idx}
