  FROM img 
 WHERE item_idx=#{item_idx}

mapper.update:
UPDATE item 
   SET item_quantity=item_quantity - #{ord_quantity}
 WHERE item_idx=#{item_idx}

mapper.select:
SELECT item_quantity
  FROM item
 WHERE item_idx=#{item_idx}

mapper.insert:
INSERT INTO ord
VALUES (
		(SELECT * 
		FROM (SELECT IFNULL(MAX(ord_idx)+1, '1')
	     	    FROM ord
	     	   ORDER BY ord_idx*1
	     	   LIMIT 1) AS A)
	   ,#{item_idx}
	   ,#{member_id}
	   ,DATE_FORMAT(now(), "%Y%m%d%H%i%s")

	   ,#{ord_reqContent}
	   ,#{ord_quantity}
	   ,'N'
	   )

INSERT INTO safe
VALUES (
	   (SELECT *
	      FROM (SELECT IFNULL(MAX(safe_idx)+1,'1') 
	      		  FROM safe
	      		 ORDER BY safe_idx*1 DESC
	      		 LIMIT 1) temp)
	   ,#{ord.item_idx}
	   ,(SELECT ord_idx 
	       FROM ord 
	      WHERE item_idx=#{ord.item_idx} 
	        AND member_id=#{ord.member_id}
	        AND ord_reqContent=#{ord.ord_reqContent}
	        AND ord_quantity=#{ord.ord_quantity} 
	        AND ord_date=#{ord_date}
	    )
	   ,#{ord.member_id}
	   ,(SELECT s.shop_idx 
	       FROM shop s, item i
	      WHERE s.shop_idx=i.shop_idx
	        AND i.item_idx=#{ord.item_idx})
	   ,#{item_price}*#{ord.ord_quantity}
	   ,'N'
	   )

INSERT INTO coin
VALUES (
	   (SELECT * FROM
	   (SELECT IFNULL(MAX(coin_idx)+1, '1')
		  FROM coin
		 ORDER BY coin_idx+1 DESC
		 LIMIT 1) AS A)
		,#{ord.member_id}	
		,''		
		,#{item_price} * #{ord.ord_quantity}		 
		,#{coin_total}
		,DATE_FORMAT(now(), "%Y%m%d%H%i%s")
	   )

