 WHERE item_idx=#{item_idx}
   AND member_id=#{member_id}

mapper.select:
SELECT COUNT(item_idx) 
  FROM recentView
 WHERE member_id = #{member_id} 
   AND item_idx = #{item_idx}

insert.if:
